mapper.select:
	select 

	 from tbl_ssss_city_merchant where parent_city_merchant_id = #{merchantId,jdbcType=BIGINT}

  	and create_date_time > #{startTime,jdbcType=TIMESTAMP} and create_date_time  < #{endTime,jdbcType=TIMESTAMP}


    	and  status = #{status,jdbcType=TINYINT}


  	and (city_merchant_name like concat('%', #{keywords}, '%') or merchant_code like concat('%', #{keywords}, '%'))

   order by create_date_time desc
