mapper.select:
select
from tbl_ssss_order s left join tbl_ssss_city_merchant m on s.merchant_id=m.id where s.merchant_id = #{merchantId,jdbcType=BIGINT}
and s.create_date_time > #{startTime,jdbcType=TIMESTAMP} and s.create_date_time < #{endTime,jdbcType=TIMESTAMP}
and s.status = #{status,jdbcType=TINYINT},
order by s.create_date_time desc

select 

from tbl_ssss_order
where status = 0

		and create_date_time > #{startTime,jdbcType=TIMESTAMP} and create_date_time  < #{endTime,jdbcType=TIMESTAMP}


		and merchant_id=#{merchantId}

	order by update_time desc

select 

from tbl_ssss_order
where 
	ssss_id = #{ssssId,jdbcType=BIGINT} 
	and order_number=#{orderNumber,jdbcType=VARCHAR} 
	and order_type=#{orderType,jdbcType=TINYINT}

select
from tbl_ssss_order s left join tbl_ssss_city_merchant m on s.merchant_id=m.id where s.merchant_id = #{merchantId,jdbcType=BIGINT}
and s.create_date_time > #{startTime,jdbcType=TIMESTAMP} and s.create_date_time < #{endTime,jdbcType=TIMESTAMP}
and s.status = #{status,jdbcType=TINYINT},
and (m.city_merchant_name like concat('%', #{keywords}, '%') or m.merchant_code like concat('%', #{keywords}, '%') or s.order_number like concat('%', #{keywords}, '%'))
order by s.create_date_time desc
